import React, { useState } from 'react';
import { Table, Grid } from 'semantic-ui-react';
import colors from '../../colors';
import { DeleteIcon, AddIcon } from '../icons/Icons';

const rowStyle = {
  backgroundColor: colors.mid
};

export const BudgetGroup = () => {
  const [hovering, setHovering] = useState(false);
  const noPaddingRight = { paddingRight: '0px' };
  const noPaddingLeft = { paddingRight: '0px' };


  return (
    <Table.Row 
      style={rowStyle} 
      onMouseEnter={() => setHovering(true)} 
      onMouseLeave={() => setHovering(false)}
    >
      <Table.Cell>
        <Grid divided columns={3}>
          <Grid.Column width={1} style={noPadding}>
            <DeleteIcon hovering={hovering} />
          </Grid.Column>
          <Grid.Column stretched width={14}>Group name</Grid.Column>
          <Grid.Column width={1} style={noPadding}>
            <AddIcon hovering={hovering} />
          </Grid.Column>
        </Grid>
      </Table.Cell>
      <Table.Cell>Budgeted</Table.Cell>
      <Table.Cell>Spent</Table.Cell>
      <Table.Cell>Balance</Table.Cell>
    </Table.Row>
  )
}
